class Solution {
public:
    bool areSentencesSimilar(string s1, string s2) {
        if(s1.size() < s2.size()) swap(s1, s2);
        vector<string> ss1 = split(s1), ss2 = split(s2);
        return solve(ss1, ss2, 0, 0);
    }

private:
    bool solve(vector<string> &s1, vector<string> &s2, int i, int j, bool inserted = false){
        int n = s1.size();
        if(i == n){
            return j == s2.size();
        }
        if(s1[i].compare(s2[j]) == 0){ // ith == jth
            if(inserted){ // if remaining string matches then you got a match return TRUE
                bool substringMatched = compare(s1, s2, i, j);
                if(substringMatched) return true;
            }
            bool val1 = solve(s1, s2, i+1, j, true); // we may or may not have already inserted in between skip ith and solve ahead
            if(!inserted){ // till now we haven't already inserted don't insert here too  [Match ith to jth] and solve ahead
                val1 |= solve(s1, s2, i+1, j+1, false);
            }
            return val1;
        }
		// we don't have choice insert ith and solve ahead
        return solve(s1, s2, i+1, j, true);
    }

    bool compare(const vector<string> &v1, const vector<string> &v2, int i, int j){
        int n1 = v1.size(), n2 = v2.size();
        while(i < n1 && j < n2){
            if(v1[i++] == v2[j++]) continue;
            return false;
        }
        return i == n1 && j == n2;
    }

    vector<string> split(string &sr){
        stringstream ss(sr);
        vector<string> tokens;
        string token;
        while(getline(ss, token, ' ')){
            tokens.push_back(token);
        }
        return tokens;
    }
};
