        vector<string>ans;
        int start=0,prev=0;
        while(start<sentence.size()){
            if(sentence[++start]==' '){
                ans.push_back(sentence.substr(prev,start-prev));
                prev=++start;
            } else if(start==sentence.size()){
                ans.push_back(sentence.substr(prev));
            }
        }
        return ans;
    }
public:
