        if(s2.size()>s1.size()){
            swap(s1,s2);
        }
        if(s1.size()==s2.size()){
            return s1==s2;
        } else {
            vector<string>first=parse(s1);
            vector<string>second=parse(s2);
            int start=0,end=second.size()-1,startCnt=0,endCnt=0;
            for(int i=0;i<first.size();i++){
                if(first[i]==second[start]){
                    if(++start==second.size()){
                        return true;
                    }
                    startCnt++;
                } else {
                    break;
                }
            }
            for(int i=first.size()-1;i>=0;i--){
                if(first[i]==second[end]){
                    if(--end<0){
                        return true;
                    }
                    endCnt++;
                } else {
                    break;
                }
            }
            return startCnt+endCnt==second.size();
        }
    }
};

