func ok(s, t []string) bool {
    n, m := len(s), len(t)
    i := 0
    for i < n && i < m {
        if s[i] == t[i] {
            i++
        } else {
            break
        }
    }

    j, k := n - 1, m - 1
    for j >= 0 && k >= 0 {
        if s[j] == t[k] {
            j--
            k--
        } else {
            break
        }
    }

    return i > k
}

func areSentencesSimilar(s1 string, s2 string) bool {
    ss1, ss2 := strings.Split(s1, " "), strings.Split(s2, " ")
    return len(ss1) >= len(ss2) && ok(ss1, ss2) || len(ss1) < len(ss2) && ok(ss2, ss1)
}

