```cpp
class Solution {
public:
    vector<string> s1, s2;
    void opt(string in_str, vector<string> &v) {
        stringstream ss;
        ss << in_str;
        string w;
        while (ss >> w) {
            v.push_back(w);
        }
        return;
    }
    bool areSentencesSimilar(string sentence1, string sentence2) {
        opt(sentence1, s1); opt(sentence2, s2);
        int len1 = s1.size();
        int len2 = s2.size();
        int i1 = 0;
        int i2 = 0;
        bool fi = false;
        bool fj = false;
        while (i1 < len1 && i2 < len2) {
            if (s1[i1] == s2[i2]) {
                i1++; i2++;
                fi = true;
            }
            else {
                break;
            }
        }
        int j1 = len1 - 1;
        int j2 = len2 - 1;
        while (j1 >= 0 && j2 >= 0) {
            if (s1[j1] == s2[j2]) {
                j1--; j2--;
                fj = true;
            }
            else {
                break;
            }
        }
        if (i1 > j1 || i2 > j2) {
            if (fi || fj) {
                return true;
            }
        }
        return false;
    }
};
```

