use std::collections::*;

impl Solution {
  pub fn are_sentences_similar(sentence1: String, sentence2: String) -> bool {
    let mut s1 = sentence1.split(" ").into_iter().map(|v| v.to_string()).collect::<VecDeque<String>>();
    let mut s2 = sentence2.split(" ").into_iter().map(|v| v.to_string()).collect::<VecDeque<String>>();

    if s1.len() < s2.len() {
      std::mem::swap(&mut s1, &mut s2);
    }

    while !s2.is_empty() {
      if s1[0] == s2[0] {
        s1.pop_front();
        s2.pop_front();
      } else {
        break
      }
    }

    while !s2.is_empty() {
      if s1[s1.len()-1] == s2[s2.len()-1] {
        s1.pop_back();
        s2.pop_back();
      } else {
        break
      }
    }

    s2.is_empty()
  }
}
