	public boolean areSentencesSimilar(String sentence1, String sentence2) {
        var s1 = sentence1.length() <= sentence2.length() ? sentence1 : sentence2;
        var s2 = sentence1.length() <= sentence2.length() ? sentence2 : sentence1;

        var w1 = s1.split(" ");
        var w2 = s2.split(" ");

