```Go
func areSentencesSimilar(sentence1 string, sentence2 string) bool {

    ss1, ss2 := strings.Fields(sentence1), strings.Fields(sentence2)
    if len(ss2) > len(ss1) {
        ss1, ss2 = ss2, ss1
    }

    i := 0
    for ; i < len(ss2) && ss1[i] == ss2[i]; i++ {}

    j := 1
    for ; len(ss2) - j >= i && ss2[len(ss2)-j] == ss1[len(ss1)-j]; j++ {}
    j--
    return i + j == len(ss2)
}
