class Solution:
    def areSentencesSimilar(self, a: str, b: str) -> bool:
        if len(b) == 0 or a == b:
            return True
        a = a.split()
        b = b.split()

        if len(a) < len(b):
            a, b = b, a

        i = 0
        while i < len(b) and a[i] == b[i]:
            i += 1

        return i == len(b) or a[-(len(b) - i):] == b[i:]
