impl Solution {
    pub fn are_sentences_similar(sentence1: String, sentence2: String) -> bool {
        if &sentence1 == &sentence2 {
            return true;
        }
        let (mut s1, mut s2) : (Vec<&str>, Vec<&str>) = if sentence1.len() > sentence2.len() {
            (sentence1.split(" ").collect(), sentence2.split(" ").collect())
        } else {
            (sentence2.split(" ").collect(), sentence1.split(" ").collect())
        };
        let mut start = 0;
        let mut end = 0;
        for str in &s2 {
            if *str == s1[start] {
                start += 1;
            } else {
                break;
            }
        }
        loop {
            if end < s2.len() && s1[s1.len() - 1 - end] == s2[s2.len() - 1 - end] {
                end += 1;
            } else {
                break;
            }
        }
        end + start >= s2.len()
    }
}
