    static boolean match (String sentence1 , String sentence2) {
//        sentence1 = sentence1.toLowerCase();
//        sentence2 = sentence2.toLowerCase();
        String[] s1 = sentence1.split(" ");
        String[] s2 = sentence2.split(" ");
        int i = 0;
        int j = 0;
        while (i < s1.length && j < s2.length &&  s1[i].equals(s2[j])) {
            i++;
            j++;
        }
        if (i == s1.length || j == s2.length) {
            return true;
        }
        else if (s1.length > s2.length) {
            while(i < s1.length  && !s1[i].equals(s2[j])) {
                i++;
            }
            if (i == s1.length) {
                return false;
            }
            if (j == s2.length - 1) {
                return s1[s1.length - 1].equals(s2[j]);
            }else {
                while (i < s1.length - 1 && s1[i].equals(s1[i + 1])) i++;
                while (i < s1.length && j < s2.length) {
                    if (!s1[i].equals(s2[j])) {
                        return false;
                    }
                    i++;
                    j++;
                }
                return (i == s1.length && j == s2.length);
            }
        }
        else  {
            while(j < s2.length  && !s2[j].equals(s1[i])) {
                j++;
            }
            if (j == s2.length) {
                return false;
            }
            if (i == s1.length - 1) {
                return s2[s2.length - 1].equals(s1[i]);
            }else {
                while (j < s2.length - 1 && s2[j].equals(s2[j + 1])) j++;
