public class Solution 
{
    public bool AreSentencesSimilar(string sentence1, string sentence2) 
    {
        string[] s1 = sentence1.Split(' '), s2 = sentence2.Split(' ');
        return CommonPrefix(s1, s2) + CommonPrefix(s1.Reverse(), s2.Reverse()) >= Math.Min(s1.Length, s2.Length);

        static int CommonPrefix(IEnumerable<string> s1, IEnumerable<string> s2) 
            => s1.Zip(s2).TakeWhile(p => p.First == p.Second).Count();
    }
}
