class Solution {
public:
  bool areSentencesSimilar(string s1, string s2) {
    if(s1.size() < s2.size()) swap(s1,s2);                         //done s2 - the smallest
    int i = 0, j = s2.size()-1, id = s1.size()-1, last_space = 0;

    while(i != s2.size() && s1[i] == s2[i]) 
      if(s1[i++] == ' ') last_space = i;

    if(i == s2.size() && (i == s1.size() || s1[i] == ' ') ) return true;
    i = last_space - 1; 

    while(j > i && s1[id] == s2[j]) id--, j--;

    return j == i && s1[id] == ' ';
  }
};
