class Solution {
    public boolean areSentencesSimilar(String s, String t) {
        int ms = s.length(), nt=t.length();
        if(s==null||ms==0||t==null||nt==0){
            return true;
        }

        if(ms<nt) return areSentencesSimilar(t,s);

        String[] S = s.split(" ");
        String[] T = t.split(" ");

        int m = S.length, n = T.length;

        int i=0;
        while(i<n&&(S[i]).equals(T[i])) i++;
        if(i==n) return true;

        int j=0;
        while(j<n&&(S[m-1-j]).equals(T[n-1-j])) j++;
        if(j==n) return true;

        return (i+j)==n;

    }
}
