	if(head == null) return head;
	ListNode dummy = new ListNode(0), pre = dummy, curr = head;
	pre.next = curr;
	while(curr != null) {
		while(curr.next != null && curr.val == curr.next.val) 
			curr = curr.next;                   // curr goes to next node until it finds new val which is not same as prev one
		if(pre.next == curr) pre = pre.next;    // no duplicate, so move pre to next node
		else pre.next = curr.next;              // skip duplicate but don't move pre
		curr = curr.next;                       // update curr
	}
	return dummy.next;
}
