class Solution {
public:
    ListNode* deleteDuplicates(ListNode* head) {
        //recursion exit condition
        if (!head||!head->next) return head;

        ListNode* node = head->next;

        //case: node val is not a duplicate of head val
        if (node->val != head->val) {
            //repeat process with next two nodes
            head->next = deleteDuplicates(node);
            return head;
        } 
        //case: node val is a duplicate of head val
        else {
            //remove all nodes until no duplicate value as head, or end of list
            while (node && node->val == head->val) node = node->next;
            //return node here because head is part of the duplicate
            return deleteDuplicates(node);
        }
    }
};
