               cnt++;
           }
            //disconnect link with cur
           temp -> next = NULL;
            //if count is 1 
            //that means it is not repeated
           if(cnt == 1){    
             iter -> next = temp;
             iter = iter -> next;
           }
        }
        return dummy -> next;
    }
