class Solution {
    func deleteDuplicates(_ head: ListNode?) -> ListNode? {
        guard let head = head else { return nil }
        // Declare a dummy node to start from it and later use it to return the result.
        let dummy: ListNode? = ListNode(Int.min)
        // Connect dummy with the head
        dummy?.next = head
        // Use two pointer approach to be able to delete nodes in between them.
        var previous: ListNode? = dummy
        var current: ListNode? = dummy?.next

        // Iterate through all nodes in the list
        while current != nil {
            // If we have a duplicate
            if current?.next?.val == current?.val {
                // Iterate until current points to the last duplicated node, needed if there's more than 2 duplicates in a row
                while current?.next?.val == current?.val {
                    current = current?.next
                }
                // Remove duplicates by connecting previous with the node after last duplicate
                previous?.next = current?.next
                // Move current pointer to the next node to continue looping
                current = current?.next
            } else {
                // if the duplicate condition didn't trigger, continue looping
                previous = current
                current = current?.next
            }
        }
        // The result is dummy's next node
        return dummy?.next
    }
}
