    public ListNode deleteDuplicates(ListNode head) {
        if(head == null) return null;

        ListNode newDummy = new ListNode(-1); // the dummy head of the non-duplicate linked list
        ListNode nonDup = newDummy; // the pointer used to traverse non-duplicate linked list

        ListNode node = head; // the pointer used to traverse the original linked list

        while(node != null){
            if(node.next != null && node.val == node.next.val){
                int dupVal = node.val; // record the value of duplicates
                while(node != null && node.val == dupVal){
                    node = node.next; // skip all the duplicates
                }
            }else{
                nonDup.next = node;
                nonDup = nonDup.next;
                node = node.next;
            }
        }

        nonDup.next = null; // cut the following nodes referenced by node.next ...

        return newDummy.next;
    }
