class Solution {
public:
    int signFunc(long long product){
        if(product < 0){
            return -1;
        }
        else if(product > 0){
            return 1;
        }
        else{
            return 0;
        }
    }
    int arraySign(vector<int>& n) {
        long long int product=1;

        for(auto val:n){
            product *= val*1ll;
            product = product%1000000007 ;
        }

        return signFunc(product);
    }
};
```
