class ListNode(object):
    def __init__(self, val, prev=None, next=None):
        self.val = val
        self.prev = prev
        self.next = next

class Solution:
    def findTheWinner(self, n: int, k: int) -> int:
        head = prev = ListNode(1)
        for i in range(2, n+1):
            node = ListNode(i)
            node.prev = prev
            prev.next = node
            prev = node
        head.prev = prev
        prev.next = head
        curr = head.prev
        while curr.next is not curr:
            for _ in range(k):
                curr = curr.next
            curr.prev.next = curr.next
            curr.next.prev = curr.prev
        return curr.val
