class Solution {
public:
    int findTheWinner(int n, int k) {
        if(k == 1)
            return n;
        queue<int> q; // store friends from 1 to n
        for(int i = 1; i <= n; i++)
            q.push(i);
        int count = 1, temp;
        while(!q.empty() && q.size() > 1){
            if(count == k){ // leaves the circle
                q.pop();
                count = 1;
            }//end if
            else{
                temp = q.front();
                q.pop();
                q.push(temp); // make circle via move the front to the end 
                count++;
            }//end else
        }//end while
        return q.front();
    }
};

/*
For example :
