class Solution {
public:
    int findTheWinner(int n, int k) {
        int i, idx=0, x;

        // Make a array and store the integers from 1 to n
        vector<int> nums;
        for(i=0 ; i<n ; i++)
        {
            nums.push_back(i+1);
