        // In each iteration remove the element at position x and then update the value of x 
        while(nums.size()>1)
        {
            x = (idx+k-1)%nums.size();
            nums.erase(nums.begin()+x);
            idx = (x)%nums.size();
        }
        return nums[0];
    }
};
