class Solution {
public:
    int findTheWinner(int n, int k) {

        ios_base::sync_with_stdio(false);
        cin.tie(0), cout.tie(0);

        ListNode *prev = NULL;
        int x = 1;

        // making linked list from the array 
        for(int i=n; i>=1; i--){
            ListNode *temp = new ListNode(i);
            temp->next = prev;
            prev = temp;
        }

        // making it circular linked list
        ListNode *head = prev, *curr = prev;
        while(head->next != NULL) 
            head = head->next;
        head->next = prev;

        if(k == 1)
            return n; // best case

        while(n != 1){
            if(x == k-1){
                curr->next = curr->next->next; // deleting the friend of k position
                x = 1;
                n--;
            }else
                x++;
            curr = curr->next;
        }

        return curr->val;
    }
};
