class Solution {
    public int findTheWinner(int n, int k) {
        // using linked list is easier to simulate and run faster
        LinkedList<Integer> ll = new LinkedList<>();
        for(int i = 1; i <= n; i++){
            ll.add(i);
        }

        // follow the rules and remove the loser
        int ind = 0;
        while(ll.size() > 1){
            ind = ind + k-1;
            while(ind >= ll.size())
                ind -= ll.size();
            ll.remove(ind);
        }
        return ll.getFirst();
    }
}
