class Solution {
public:
    int find_winner(int n,int k){

        if(n == 1)
            return n;
        // this is a simple math that if n people are in a circle then remainder(k%n) shows the kth
        // person starting from 1 after (int)(k/n) complete cycles.
        // if remainder is 0 that means nth person is kth person starting 
        // from 1 after (int)(k/n)  complete cycles.

        int killed = ( k % n == 0) ? n : (k % n);


        // getting the winner from (n-1,k) and mapping it with (n,k)
        // so mapping is like this:-
        // next person after killed person in circle(n,k) will be 1st person in new circle(n-1,k)
        // so suppose winner of the child circle (n-1,k) is w, then in the 
        // parent circle (n,k), new winner is killed of(n,k) + w( that is winner(n-1,k) )
        // sometimes what happens is that killed(n,k) + w( that is winner(n-1,k) ) becomes more than n
        // that is why we want modulo of position of new winner with n once again

        int winner = killed + find_winner(n-1,k);
        return (winner % n == 0) ? n : (winner % n);
    }
    int findTheWinner(int n, int k) {

        return find_winner(n,k);
    }
};

