class Solution {
    public int findTheWinner(int n, int k) {        
        List<Integer> list = new ArrayList<>();
        for (int i=0; i<n; i++) {
            list.add(i+1);
        }

        int ans = helper(list, k - 1, 0);

        return ans;
    }

    private static int helper(List<Integer> list, int k, int index) {
        int size = list.size();

        if (size == 1) {
            return list.get(0);
        }

        index = (index + k) % size;
        list.remove(index);

        return helper(list, k, index);
    }
}
