class Solution {
public:
    int findTheWinner(int n, int k) {

        //Temp array to hold the active candidates.
        vector<int> circular(n);        
        for(int i = 0; i< n; i++){
            circular[i] = i+1;            
        }


        int start = 0;        
        while(true){      
            //Check if only one element is remaining.
            if(circular.size() ==  1)
                break;

            start += (k-1);                         //Add k to current (start is inclusive.)
            start %= circular.size();               //Array size is changing, remain in boundary
            circular.erase(circular.begin() + start);//Erase the selected candidate.
        }

        return circular[0]; //Return the winner, only 1st element.                
    }
};
