class Solution {
 static int round(ArrayList<Integer> arr , int pointer , int k){
      if(arr.size()==2){
          if(pointer==0){
              if(k%2==0){
                  return arr.get(0);
              }else{
                  return arr.get(1);
              }
          }else {
              if(k%2==0){
                  return arr.get(1);
              }else {
                  return arr.get(0);
              }
          }
      }
      for(int i=1;i<k;i++){
          pointer++;
          if(pointer==arr.size()){
              pointer=0;
          }
      }
      arr.remove(pointer);
      if(pointer==arr.size()){
          pointer=0;
      }
      return round(arr,pointer,k);
    }
