class Solution(object):
    def minSideJumps(self, obstacles):
        path,res,i = [2],0,0
        while i < len(obstacles):
            if obstacles[i] in path:
                if len(path) == 1:
                    res+=1
                    if obstacles[i] == 1:
                        path= [2,3]
                    if obstacles[i] == 2:
                        path = [1,3]
                    if obstacles[i] == 3:
                        path = [1,2]
                    i-=1
                if len(path)==2:
                    if path[0] == obstacles[i]:
                        path = [path[1]]
                    elif path[1] == obstacles[i]:
                        path.pop()
            i+=1
        return res
