```
class Solution {
public:
    int minSideJumps(vector<int>& obstacles) {
        int n = obstacles.size();
        vector<vector<int>> v(3, vector<int>(n, n)); //v[k][i] -> position of next obstacle on lane k if currently standing at position i
        for (int i=n-2; i >=0; i--)
        {
            for (int k=0; k<3; k++)
            {
                v[k][i] = v[k][i+1];
            }
            if (obstacles[i]) v[obstacles[i]-1][i] = i;
        }
        int ret = 0;
        for (int i=0, lane=1; i<n-1; i++)
        {
            if (obstacles[i+1] - 1 == lane)
            {
                if (v[(lane+1)%3][i] > v[(lane+2)%3][i]) lane = (lane+1)%3;
                else lane = (lane+2)%3;
                ret++;
            }
        }
        return ret;
    }
};
```

