class Solution {
public:

    void fill_lan(vector<int> obstacles,int lan_no,vector<int>& lan){
        int n = obstacles.size();
        lan[n-1] = -1;
        for(int i=n-2;i>=0;i--){
            if(obstacles[i]==lan_no){
                lan[i] = 0;
            }else{
                if(lan[i+1]!=-1) lan[i] = lan[i+1]+1;
                else lan[i] = -1;
            }
        }
    }

    int minSideJumps(vector<int>& obstacles) {

        int n = obstacles.size();

        vector<int> lan1(n,0);
        vector<int> lan2(n,0);
        vector<int> lan3(n,0);

        fill_lan(obstacles,1,lan1);
        fill_lan(obstacles,2,lan2);
        fill_lan(obstacles,3,lan3);

        int res = 0;
        int curr_lan = 2;

        for(int i=0;i<n-1;i++){
            if(curr_lan==obstacles[i+1]){
                if(curr_lan==1){
                    if(lan2[i]==-1){
                        res++;
                        curr_lan = 2;
                    }else if(lan3[i]==-1){
                        res++;
                        curr_lan = 3;
                    }else if(lan2[i]<lan3[i]){
                        res++;
                        curr_lan = 3;
                    }else if(lan2[i]>lan3[i]){
                        res++;
                        curr_lan = 2;
                    }
                }else if(curr_lan==2){
                    if(lan1[i]==-1){
                        res++;
                        curr_lan = 1;
                    }else if(lan3[i]==-1){
                        res++;
                        curr_lan = 3;
                    }else if(lan1[i]<lan3[i]){
                        res++;
                        curr_lan = 3;
                    }else if(lan1[i]>lan3[i]){
                        res++;
                        curr_lan = 1;
                    }
                }else if(curr_lan==3){
                    if(lan1[i]==-1){
                        res++;
                        curr_lan = 1;
                    }else if(lan2[i]==-1){
                        res++;
                        curr_lan = 2;
                    }else if(lan1[i]<lan2[i]){
                        res++;
                        curr_lan = 2;
                    }else if(lan1[i]>lan2[i]){
                        res++;
                        curr_lan = 1;
                    }
                }
            }
        }

        return res;

    }
};
```

