class Solution {
public:
    int minSideJumps(vector<int>& obstacles) {

        //for every point we are calculating the minimum sideway jumps that can be jump to the a lane from thr other two lanes

        vector<int> jumps(3,0);
        jumps[0]=1; //at first frog is in lane 2 so it requires 1 sideway jump 
        //to get on to lane 1 (1-based indexing)
        jumps[2]=1; 

        for(int k=0;k<obstacles.size();k++)
        {
            if(obstacles[k]>0)
            {
                //a obstacle is present make the value in the current lane to infinite 
                jumps[obstacles[k]-1]=1e6;
            }
            //for every lane check whether other way exists to get on to the current lane from other lane which has minimum sideway jumps than the current
           for(int i=0;i<3;i++)
           {
               if(i!=obstacles[k]-1) {//if the obstacle is not present in current lane
               //i+1%3 and i+2%3 returns the other two lanes indices (0-based)

               //and also add 1 as we are jumping from other lane to current lane
               //ie., minimum of current sideway jumps to reach the current lane 
               //and the other lanes sideway jumps+1
                jumps[i]=min(jumps[i],min(jumps[(i+1)%3],jumps[(i+2)%3])+1);
               }
           }
        }
        //have reached point n
        //jumps[0] stores the sideway jumps to get on to lane 0
        //jumps[1] stores the sideway jumps to get on to lane 1
        //jumps[2] stores the sideway jumps to get on to lane 2
        //get the minimum of them
        return min(jumps[0],min(jumps[1],jumps[2]));
    }
};
```
