        for(int index = n - 2; index >= 0; index--){
            for(int lane = 1; lane <= 3; lane++){
                if(obstacles[index + 1] != lane){
                    curr[lane] = next[lane];
                }else{
                    int ans = mod;
                    for(int i = 1; i <= 3; i++){
                        if(lane != i && obstacles[index] != i) ans = Math.min(ans, 1 + next[i]);
                    }
                    curr[lane] = ans;
                }
            }
            next = curr;
        }
        return Math.min(next[1] + 1, Math.min(next[2], next[3] + 1));
    }
}
