                if (j != x)                               // 🚫 disclude possibility due to pre obstacle 🍎 at col j
                    for (auto k{ 0 }; k < 3; ++k)
                        pre[j] = min(pre[j], 1 + pre[k]); // 💰 +1 penalty for side-hops from col j -> k
                // cur row i: best 👇 forward-hops from row i - 1 -> i
                if (j != y)                               // 🚫 disclude possibility due to cur obstacle 🍏 at col j
                    cur[j] = min(cur[j], pre[j]);         // ✅ no penalty for forward-hops from row i - 1 -> i
            }
            pre.swap(cur);                                // 🤔 memo
        }
        return *min_element(pre.begin(), pre.end());      // 🎯 minimum 👈 👉 side-hops to reach row N - 1
    }
};
