class Solution {
public:

    int help(int point,int lane,vector<int>&obstacles,vector<vector<int>>&dp){


        if(point>=obstacles.size()-1){
            return dp[point][lane]=0;
            //this is the base case
        }

        if(dp[point][lane]!=-1){
            return dp[point][lane];
        }



        if(obstacles[point+1]==0||obstacles[point+1]!=lane){
            //current lane me hi i will move one step formward for the most optimal answer 
            //as i need to optimse the n umber of side jumps overall
             dp[point][lane]=help(point+1,lane,obstacles,dp);
        }else{

            int c=obstacles[point+1];
            int mn=INT_MAX;
            if(c!=1 && obstacles[point]!=1){
                mn=min(mn,help(point+1,1,obstacles,dp)+1);

            }
            if(c!=2 && obstacles[point]!=2){
                mn=min(mn,help(point+1,2,obstacles,dp)+1);

            }
            if(c!=3 && obstacles[point]!=3){
                mn=min(mn,help(point+1,3,obstacles,dp)+1);
            }
             dp[point][lane]=mn;
        }
        return dp[point][lane];
    }
    int minSideJumps(vector<int>& obstacles) {
        //note you should first read the question carefully 

        if(obstacles.size()<=1){
            return 0;
        }
        //when ever he makes a sideway jump he makes it to a diff lane on the same point
        int n=obstacles.size();
        //now we willl optimse it 
        //see which all variables are changing in the current recursiuve 
        //logic building and implementation both somewhat diff
        vector<vector<int>>dp(n+2,vector<int>(4,-1));
        //assign meaning to the dp matrix 
        //dp[i][j]-min number of side way jumps to reach the last points


        return help(0,2,obstacles,dp);

        //there are no obstacles on point 0 to n


    }
};
