class Solution {
    class Node {
        int i;
        int j;
        int step;

        public Node(int i, int j, int step) {
            this.i = i;
            this.j = j;
            this.step = step;
        }
    }

    public int minSideJumps(int[] obstacles) {
        int N = obstacles.length;
        boolean visited[][] = new boolean[3][N];

        Queue<Node> queue = new LinkedList<>();
        queue.add(new Node(1, 0, 0));
        visited[1][0] = true;

        while(!queue.isEmpty()) {
            Node curr = queue.poll();

            if(curr.j == N - 1) {
                return curr.step;
            }

            if(curr.i != obstacles[curr.j + 1] - 1 && !visited[curr.i][curr.j + 1]) {
                // jump forward
                visited[curr.i][curr.j + 1] = true;
                queue.add(new Node(curr.i, curr.j+1, curr.step));
            }

            int rowAfterJump = (curr.i + 1) % 3;
            if(rowAfterJump != obstacles[curr.j] - 1 && !visited[rowAfterJump][curr.j]) {
                // jump to next lane
                visited[rowAfterJump][curr.j] = true;
                queue.add(new Node(rowAfterJump, curr.j, curr.step + 1));
            }

            rowAfterJump = (curr.i + 2) % 3;
            if(rowAfterJump != obstacles[curr.j] - 1 && !visited[rowAfterJump][curr.j]) {
                // jump to next next lane
                visited[rowAfterJump][curr.j] = true;
                queue.add(new Node(rowAfterJump, curr.j, curr.step + 1));
            }
        }

         return 0;
    }
}
