class Solution {
    public int minSideJumps(int[] obstacles) {
        int res = 0;
        int n = obstacles.length - 1;
        int curLane = 2;

       Map<String, Integer> map = new HashMap<>();
        return findMinDistance(0, 2, obstacles, n, map);
    }

    private int findMinDistance(int pos, int curLane, int[] obstacles, int n, Map<String, Integer> map) {

        if(map.containsKey(pos + "_" + curLane)) {
            return map.get(pos + "_" + curLane);
        }
        int curPos = pos;
        while(pos < n && obstacles[pos+1] != curLane) {
            pos++;
        }

        if(pos == n) {
            return 0;
        }

        if(obstacles[pos] == 0) {
		// multiple options available : choose min of both possible lanes where we can choose to go
            if(curLane == 1) {
                int res = 1 + Math.min(findMinDistance(pos+1, 2, obstacles, n, map), findMinDistance(pos+1, 3, obstacles, n, map));
                map.put(curPos + "_" + curLane, res);
                return res;
            } else if (curLane == 2) {
                int res = 1 + Math.min(findMinDistance(pos+1, 1, obstacles, n, map), findMinDistance(pos+1, 3, obstacles, n, map));
                map.put(curPos + "_" + curLane, res);
                return res;
            } else if (curLane == 3) {
                int res = 1 + Math.min(findMinDistance(pos+1, 1, obstacles, n, map), findMinDistance(pos+1, 2, obstacles, n, map));
                map.put(curPos + "_" + curLane, res);
                return res;
            }
        } else if(obstacles[pos] == 1) {
		// go to the only posssible lane
            if(curLane == 2) {
                int res = 1 + findMinDistance(pos+1, 3, obstacles, n, map);
                map.put(curPos + "_" + curLane, res);
                return res;
            } else if (curLane == 3) {
                int res = 1 + findMinDistance(pos+1, 2, obstacles, n, map);
                map.put(curPos + "_" + curLane, res);
                return res;
            }
        } else if(obstacles[pos] == 2) {
		// go to the only posssible lane
            if(curLane == 1) {
                int res = 1 + findMinDistance(pos+1, 3, obstacles, n, map);
                map.put(curPos + "_" + curLane, res);
                return res;
            } else if (curLane == 3) {
                int res = 1 + findMinDistance(pos+1, 1, obstacles, n, map);
                map.put(curPos + "_" + curLane, res);
                return res;
            }
        } else if(obstacles[pos] == 3) {
		// go to the only posssible lane
            if(curLane == 2) {
                int res = 1 + findMinDistance(pos+1, 1, obstacles, n, map);
                map.put(curPos + "_" + curLane, res);
                return res;
            } else if (curLane == 1) {
                int res = 1 + findMinDistance(pos+1, 2, obstacles, n, map);
                map.put(curPos + "_" + curLane, res);
                return res;
            }
        }

        throw new IllegalArgumentException("shouldn't have reached here!!");
    }
}
