        if(dp[idx][lane] != -1) return dp[idx][lane];

        int ans = 1e6;

        if(idx+1 < n && obstacles[idx+1] == lane){

            if(lane == 1){
                ans = min(ans, 1 + f(idx, 2, n, obstacles, dp));
                ans = min(ans, 1 + f(idx, 3, n, obstacles, dp));
