class Solution {
public:
    int minSideJumps(vector<int>& obstacles) {
        int n=obstacles.size();
        vector<vector<int>> D(3, vector<int>(n, 0));
        for(int i=n-2; i>=0; --i)
        {
            if(obstacles[i]==0)
            {
                D[0][i]=min(D[0][i+1], min(D[1][i+1], D[2][i+1])+1); 
                D[1][i]=min(D[1][i+1], min(D[0][i+1], D[2][i+1])+1); 
                D[2][i]=min(D[2][i+1], min(D[0][i+1], D[1][i+1])+1);
            }
            else if(obstacles[i]==1)
            {
                D[0][i]=n;
                D[1][i] = min(D[1][i+1], D[2][i+1]+1);
                D[2][i] = min(D[2][i+1], D[1][i+1]+1);
            }
            else if(obstacles[i]==2)
            {
                D[1][i]=n;
                D[0][i] = min(D[0][i+1], D[2][i+1]+1);
                D[2][i] = min(D[2][i+1], D[0][i+1]+1);
            }
            else if(obstacles[i]==3)
            {
                D[2][i]=n;
                D[0][i] = min(D[0][i+1], D[1][i+1]+1);
                D[1][i] = min(D[1][i+1], D[0][i+1]+1);
            }
        }
        return D[1][0];
    }
};
