class Solution:
    def minSideJumps(self, obstacles: List[int]) -> int:
        # inits
        cur_lane, side_steps, len_obs = 2, 0, len(obstacles)

        # O(1) lookup to obtain most empty other lane
        other_lanes = [{2:3, 3:2}, {1:3, 3:1}, {1:2, 2:1}]


        for i in range(len_obs):
            # Go until next obstacle and increment side steps
            if obstacles[i] == cur_lane:
                side_steps += 1

                # Search for obstacles in other lanes
                for j in range(i-1, len_obs):
                    if obstacles[j] in other_lanes[cur_lane-1]:

                        # Next lane is the one that doesn't have the next obstacle
                        cur_lane = other_lanes[cur_lane-1][obstacles[j]]
                        break

                # We are at the end
                if j == len_obs-1:
                    break

        return side_steps
