the basic concept which the solutions works is when there is no obstacle aahead let the frog move in the same lane and as an obstacle arrive check for options where the frog can change lanes andafter changing it has to take minimum side jump. :)
```
class Solution {
public:
    int dp[500005][3];
    int solve(vector<int>& arr, int i, int l) {
        int n = arr.size();
        if(i == n-1) {
            return 0;
        }
        if(arr[i] == l+1) {
            return 1e9;
        }
        if(dp[i][l] != -1) {
            return dp[i][l];
        }
        if(arr[i+1] != l+1) {
		//move in same lane
         dp[i][l] = solve(arr, i+1, l);
        } else {
		//change lane and check for min
         dp[i][l] = 1 + min(solve(arr, i, (l+1)%3), solve(arr, i, (l+2)%3));
        }
        return dp[i][l];
    }

    int minSideJumps(vector<int>& obstacles) {
        memset(dp, -1, sizeof(dp));
        return solve(obstacles, 0, 1);
    }
};
