public int minSideJumps(int[] obstacles) {
        int sideJumps = 0;  // track side jumps
        int currLane = 2;   // track lane that the frog is currently in
        for (int i = 0; i < obstacles.length - 1; i++) {
            if (obstacles[i+1] == currLane) {   // check if there is a obstacle right in front of the frog
                if (obstacles[i] != 0) {    // check if there is an obstacle in any of the lanes at the current point that the frog is in
                    currLane = getNextLane(obstacles[i], obstacles[i+1]);   // if so, only one lane for the frog to move to, update current lane frog is in
                } else {    // if there are no obstacles at the current point, we have 2 lanes we could jump to, so we need to choose the right one
                    // the right lane to jump to is the one that does not hit an obstacle first
                    int j = i + 2;  // j will act as a running pointer to find the next obstacle in a different lane
                    while (j < obstacles.length && (obstacles[j] == 0 || obstacles[j] == obstacles[i+1])) {     // we do not count points where there are no obstacles or if the next obstacle is in the same lane as the first obstacle we ran into
                        j++;
                    }
                    if (j < obstacles.length) { // if this condition is met, that means we found a obstacle in another lane before getting to the end
                        currLane = getNextLane(obstacles[i+1], obstacles[j]);   // update current lane frog is in
                    } else {    // if this else statement is hit, that means that we reached the end of the list without hitting anymore obstacles
                        i = obstacles.length - 1;   // update i in order to break out of the loop
                    }
                }
                sideJumps++;    // frog side jumps to get past the obstacle in front of it
            }
        }
        return sideJumps;
    }

