    int minSideJumps(vector<int>& obstacles) {

        int n = obstacles.size();

        int count = 0;
        int row = 2; 
        int i = 0;

        while(i < n){

            while(i < n && obstacles[i] != row){
                i++;
            }

            if(i < n && obstacles[i] == row){

                i--;

                if(i < n && obstacles[i] != 0){
                    count ++;
                    row = 6 - row - obstacles[i];
                }

                else{
                    count++;
                    int go1 = (row + 1) > 3 ? (row + 1 - 3) : (row + 1);
