                    while(i < n && obstacles[i] != go1 && obstacles[i] != go2){
                        i++;
                    }

                    if(i < n && obstacles[i] == go1){
                         row = go2;
                         i--;
                    }

                    if(i < n && obstacles[i] == go2){
                         row = go1;
                        i--;
                    }

                }
            }

        }

        return count;

    }
};

