**LOGIC-Apply BFS and check if x+1 is obstacle in same lane then add other lanes with jumps+1 to the queue else moving one step ahead in the same lane will be optimal.**


```
class Solution {
public:
    int minSideJumps(vector<int>& obstacles) 
    {

       int n=obstacles.size(); 
      queue<pair<pair<int,int>,int>>q;
      q.push({{2,0},0});
      set<pair<int,int>>vis;
      while(q.size()>0)
      {
          pair<pair<int,int>,int>d=q.front();
          q.pop();
          if(vis.find(d.first)!=vis.end())continue;
          vis.insert(d.first);
          int lane=d.first.first;
          int x=d.first.second;
          int jumps=d.second;
          if(x==n-1)return jumps;
          if(obstacles[x+1]!=lane)
          {
              q.push({{lane,x+1},jumps});                 //No obstacle so move forward
          }
          else 
          {
              for(int i=1;i<=3;i++)
              {
                  if(i!=lane && obstacles[x]!=i)
                  {
                    q.push({{i,x},jumps+1});   //Obstacle ahead in the current lane so jump to other
                  }
              }
          }
      }

       return -1;   
         }
};
