// DP Solution
// 1. dp[j] is the min jump to reach lane j
// 2. If there is a stone at lane j, set dp[j] to infinity.
// 3. Jump: lane 0 -> 1 or 2, lane 1 -> 0 or 2, lane 2 -> 0 or 1. 
//    So it's lane j -> (j+1) % 3 or (j+2) % 3.
// 4. The final result is the min in dp.
// Time complexity: O(N)
// Space complexity: O(1)
class Solution {
    public int minSideJumps(int[] obstacles) {
        if (obstacles == null || obstacles.length <= 1) return 0;
        final int N = obstacles.length - 1, INF = N * 2;
        int[] dp = new int[]{1, 0, 1};
        for (int i = 1; i <= N; i++) {
            int ob = obstacles[i];
            if (ob != 0) {
                dp[ob - 1] = INF;
            }
            for (int j = 0; j < 3; j++) {
                if (ob != j + 1) {
                    dp[j] = min(dp[j], dp[(j+1) % 3] + 1, dp[(j+2) % 3] + 1);
                }
            }
        }
        return min(dp[0], dp[1], dp[2]);
    }

    private int min(int a, int b, int c) {
        return Math.min(a, Math.min(b, c));
    }
}
