class Solution {
       public int minSideJumps(int[] obstacles) {
        int[] prev = new int[3];
        prev[0] = 1;
        prev[1] = 0;
        prev[2] = 1;

        for (int i = 0; i < obstacles.length; ++i) {
            int[] curr = new int[3];
            int obsIndex = obstacles[i] -1;
            if (obsIndex >= 0)
                curr[obsIndex] = Integer.MAX_VALUE;

            // move forward
            for (int j = 0; j < 3; j++) {
                if (curr[j] != Integer.MAX_VALUE && prev[j] != Integer.MAX_VALUE)
                    curr[j] = prev[j];
            }
            // move sideways - meanigful only for cell just after the obstacle.
            for (int j = 0; j < 3; ++j) {
                if (prev[j] == Integer.MAX_VALUE && curr[j] != Integer.MAX_VALUE) {
                    curr[j] = Math.min(curr[(j+1) % 3], curr[(j+2)%3]) + 1;
                }
            }
            prev = curr;
        }
        return Math.min(prev[0], Math.min(prev[1], prev[2]));
    }
}
