    def minSideJumps(self, obs) :
        dp = [1,0,1]
        for i in range(1,len(obs)-1):
            if obs[i] != 0:
                dp[obs[i]-1] = 1000000         
            if obs[i]-1 != 0:
                if dp[0]!= 1000000:
                    dp[0] = min(dp[0],dp[1]+1,dp[2]+1)
                else:
                    dp[0] = min(dp[1]+1,dp[2]+1)
            if obs[i]-1 != 1:
